            $aluno->setMatricula('4' . $aluno->getMatricula());
        //PSEI
        } else if ($aluno->getIdTipoIngresso() == 22) {
            $aluno->setMatricula('5' . $aluno->getMatricula());
        }


        return $aluno;
    }

