	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(TbbancaPeer::DATABASE_NAME);

		if ($this->isColumnModified(TbbancaPeer::BANCA_ID)) $criteria->add(TbbancaPeer::BANCA_ID, $this->banca_id);
		if ($this->isColumnModified(TbbancaPeer::MATRICULA)) $criteria->add(TbbancaPeer::MATRICULA, $this->matricula);
		if ($this->isColumnModified(TbbancaPeer::NOMEORIENTADOR)) $criteria->add(TbbancaPeer::NOMEORIENTADOR, $this->nomeorientador);
		if ($this->isColumnModified(TbbancaPeer::PRIMEIROMEMBRO)) $criteria->add(TbbancaPeer::PRIMEIROMEMBRO, $this->primeiromembro);
		if ($this->isColumnModified(TbbancaPeer::SEGUNDOMEMBRO)) $criteria->add(TbbancaPeer::SEGUNDOMEMBRO, $this->segundomembro);
		if ($this->isColumnModified(TbbancaPeer::TERCEIROMEMBRO)) $criteria->add(TbbancaPeer::TERCEIROMEMBRO, $this->terceiromembro);
		if ($this->isColumnModified(TbbancaPeer::QUARTOMEMBRO)) $criteria->add(TbbancaPeer::QUARTOMEMBRO, $this->quartomembro);
		if ($this->isColumnModified(TbbancaPeer::DATADEFESA)) $criteria->add(TbbancaPeer::DATADEFESA, $this->datadefesa);
		if ($this->isColumnModified(TbbancaPeer::RESULTADO)) $criteria->add(TbbancaPeer::RESULTADO, $this->resultado);
		if ($this->isColumnModified(TbbancaPeer::MEDIABANCA)) $criteria->add(TbbancaPeer::MEDIABANCA, $this->mediabanca);
		if ($this->isColumnModified(TbbancaPeer::TITULOMONOGRAFIA)) $criteria->add(TbbancaPeer::TITULOMONOGRAFIA, $this->titulomonografia);

		return $criteria;
	}

	/**
	 * Builds a Criteria object containing the primary key for this object.
	 *
	 * Unlike buildCriteria() this method includes the primary key values regardless
	 * of whether or not they have been modified.
	 *
	 * @return     Criteria The Criteria object containing value(s) for primary key(s).
