	public function getPeer()
	{
		if (self::$peer === null) {
			self::$peer = new TbbancaPeer();
		}
		return self::$peer;
	}

	/**
	 * Declares an association between this object and a Tbaluno object.
	 *
	 * @param      Tbaluno $v
	 * @return     Tbbanca The current object (for fluent API support)
	 * @throws     PropelException
	 */
	public function setTbaluno(Tbaluno $v = null)
	{
		if ($v === null) {
			$this->setMatricula(NULL);
		} else {
			$this->setMatricula($v->getMatricula());
		}

		$this->aTbaluno = $v;

		// Add binding for other direction of this n:n relationship.
		// If this object has already been added to the Tbaluno object, it will not be re-added.
		if ($v !== null) {
			$v->addTbbanca($this);
		}

		return $this;
	}


	/**
	 * Get the associated Tbaluno object
	 *
	 * @param      PropelPDO Optional Connection object.
	 * @return     Tbaluno The associated Tbaluno object.
	 * @throws     PropelException
	 */
	public function getTbaluno(PropelPDO $con = null)
	{
		if ($this->aTbaluno === null && (($this->matricula !== "" && $this->matricula !== null))) {
			$this->aTbaluno = TbalunoPeer::retrieveByPk($this->matricula);
			/* The following can be used additionally to
			   guarantee the related object contains a reference
			   to this object.  This level of coupling may, however, be
			   undesirable since it could result in an only partially populated collection
			   in the referenced object.
			   $this->aTbaluno->addTbbancas($this);
			 */
		}
		return $this->aTbaluno;
	}

