     * @return   TbcidadeQuery A secondary query class using the current class as primary query
     */
    public function useTbcidadeQuery($relationAlias = null, $joinType = Criteria::INNER_JOIN)
    {
        return $this
            ->joinTbcidade($relationAlias, $joinType)
            ->useQuery($relationAlias ? $relationAlias : 'Tbcidade', 'TbcidadeQuery');
    }

    /**
     * Exclude object from result
     *
     * @param   Tbestado $tbestado Object to remove from the list of results
     *
     * @return TbestadoQuery The current query, for fluid interface
     */
    public function prune($tbestado = null)
    {
        if ($tbestado) {
            $this->addUsingAlias(TbestadoPeer::ID_ESTADO, $tbestado->getIdEstado(), Criteria::NOT_EQUAL);
        }

        return $this;
    }

}
