		if ($deep) {
			if ($this->collTbalunomatriculas) {
				foreach ((array) $this->collTbalunomatriculas as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collTbalunobackups) {
				foreach ((array) $this->collTbalunobackups as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collTbalunos) {
				foreach ((array) $this->collTbalunos as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collTbpessoas) {
				foreach ((array) $this->collTbpessoas as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collTbprofessors) {
				foreach ((array) $this->collTbprofessors as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collTbalunomatriculas = null;
		$this->collTbalunobackups = null;
		$this->collTbalunos = null;
		$this->collTbpessoas = null;
		$this->collTbprofessors = null;
	}

	// symfony_behaviors behavior
	
	/**
	 * Calls methods defined via {@link sfMixer}.
	 */
	public function __call($method, $arguments)
	{
	  if (!$callable = sfMixer::getCallable('BaseTbnecesespecial:'.$method))
	  {
	    throw new sfException(sprintf('Call to undefined method BaseTbnecesespecial::%s', $method));
	  }
	
	  array_unshift($arguments, $this);
	
	  return call_user_func_array($callable, $arguments);
	}

} // BaseTbnecesespecial
