					$affectedRows += $this->aTbconceito->save($con);
				}
				$this->setTbconceito($this->aTbconceito);
			}

			if ($this->isNew() ) {
				$this->modifiedColumns[] = TbturmaAlunoPeer::ID_ALUNO;
			}

			// If this object has been modified, then save it to the database.
			if ($this->isModified()) {
