	{
		if ($con === null) {
			$con = Propel::getConnection(TbturmaPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}

		$objs = null;
		if (empty($pks)) {
			$objs = array();
		} else {
			$criteria = new Criteria(TbturmaPeer::DATABASE_NAME);
			$criteria->add(TbturmaPeer::ID_TURMA, $pks, Criteria::IN);
			$objs = TbturmaPeer::doSelect($criteria, $con);
		}
		return $objs;
	}

